problem.script:
from mitxgraders import *
grader = MatrixGrader(
    variables=['u', 'v', 'H'],
    sample_from={
        'u': RealVectors(shape=3),
        'v': RealVectors(shape=3),
        'H': SquareMatrices(symmetry='hermitian', dimension=3)
    },
    max_array_dim=0  # Students can't enter their own matrices
)
